form { max-width: 600px; margin: 2rem auto; display: flex; flex-direction: column; gap: 1rem; padding: 1rem; background: #1e1e1e; border-radius: 10px; box-shadow: 0 0 10px rgba(0,0,0,0.2); }
input, textarea { padding: 0.75rem; border: none; border-radius: 6px; }
label { font-weight: bold; margin-bottom: 0.25rem; }
button { background: #ffcc00; color: black; font-weight: bold; border: none; padding: 0.75rem; border-radius: 6px; cursor: pointer; }
